import React, { useState, useEffect, useRef } from "react";
import './todo.css';

function App() {
  const [mytext, setMytext] = useState("");
  const [myarray, setMyarray] = useState([]);
  const [editIdx, setEditIdx] = useState("");
  const inputRef = useRef(null); // Create a ref for the input element

  // Load data from localStorage on component mount
  useEffect(() => {
    const storedData = localStorage.getItem("todoList");
    if (storedData) {
      setMyarray(JSON.parse(storedData));
    }
  }, []);

  // Save data to localStorage whenever myarray changes
  useEffect(() => {
    localStorage.setItem("todoList", JSON.stringify(myarray));
  }, [myarray]);

  // Function to focus on the input text box
  const focusInput = () => {
    if (inputRef.current) {
      inputRef.current.focus();
    }
  };

  function onChangeDo(event) {
    setMytext(event.target.value);
  }

  function onClickAdd() {
    if (mytext.trim() === "") {
      // Display a warning message or handle the empty text case
      alert("Please enter a value.");
      focusInput(); // Focus on the input text box
      return;
    }

    // Add the text to the array
    setMyarray([...myarray, mytext]);

    // Reset text input and edit index
    setMytext("");
    setEditIdx("");
  }

  function removeItem(index) {
    const updatedArray = myarray.filter((_, idx) => idx !== index);
    setMyarray(updatedArray);
  }

  function editItem(index) {
    setMytext(myarray[index]);
    setEditIdx(index);
  }

  return (
    <div>
      <center>
        <br />
        <input
          type="text"
          name="txt"
          placeholder="Enter something..."
          value={mytext}
          onChange={onChangeDo}
          autoComplete="off"
          className="to-do_textbox"
          ref={inputRef} // Assign the ref to the input element
        />
        &nbsp;
        <button className="to-do_button" onClick={onClickAdd}>Add</button>
        <br />
        <b>
          <table>
            <tbody>
              {myarray.map((value, index) => (
                <tr key={index}>
                  <td>
                    {index + 1}. {value}
                  </td>
                  <td>
                    <img src="Edit.png" alt="edit" title="Edit this Text" onClick={() => editItem(index)} height="20" width="20" />
                  </td>
                  <td>
                    <img src="Delete.png" alt="delete" title="Delete this Text" onClick={() => removeItem(index)} height="20" width="20" />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </b>
      </center>
    </div>
  );
}

export default App;
