import React, { useState, useEffect } from "react";
import './todo.css';

function App() {
  const [mytext, setMytext] = useState("");
  const [myarray, setMyarray] = useState([]);
  const [editIdx, setEditIdx] = useState("");

  // Load data from localStorage on component mount
  useEffect(() => {
    const storedData = localStorage.getItem("todoList");
    document.querySelector('.to-do_textbox').focus();
    if (storedData) {
      setMyarray(JSON.parse(storedData));
    }
  }, []);

  // Save data to localStorage whenever myarray changes
  useEffect(() => {
    localStorage.setItem("todoList", JSON.stringify(myarray));
  }, [myarray]);

  // Generate random color codes
  const generateRandomColor = () => {
    const letters = '0123456789ABCDEF';
    let color = '#';
    for (let i = 0; i < 6; i++) {
      color += letters[Math.floor(Math.random() * 16)];
    }
    return color;
  };

  function onChangeDo(event) {
    setMytext(event.target.value);
  }

  function removeAll() {
    setMyarray([]);
  }

  function onClickAdd() {
    document.querySelector('.to-do_textbox').focus();
    if (mytext.trim() === "") {
      // Display a warning message or handle the empty text case
      alert("Please enter a value.");
      return;
    }

    // Add the text to the array
    setMyarray([...myarray, mytext]);

    // Reset text input and edit index
    setMytext("");
    setEditIdx("");
  }

  function removeItem(index) {
    const updatedArray = myarray.filter((_, idx) => idx !== index);
    setMyarray(updatedArray);
  }

  function editItem(index) {
    setMytext(myarray[index]);
    setEditIdx(index);
    document.querySelector('.to-do_textbox').focus();
  }

  return (
    <div>
      <center>
        <br />
        <div style={{"background-color":"red"}}>
          <input type="text" name="txt" placeholder="Enter something..." value={mytext} onChange={onChangeDo} autoComplete="off" className="to-do_textbox" />
          <br/>
          {window.innerWidth < 600 && <button className="to-do_button" onClick={onClickAdd} >Add</button>}
        </div>
        <br /><br />
        <b>
          <table className="to-do_table">
            <tbody>
              {myarray.map((value, index) => (
                <tr key={index} className="to-do_item" style={{ backgroundColor: generateRandomColor() }}>
                  <br />
                  <td style={{ maxWidth: '85%' }}>
                    {index + 1}. &nbsp;&nbsp;{value}
                  </td>
                  <td>
                    <span className="action_buttons1">
                      <img src="Edit.png" alt="edit" title="Edit this Text" onClick={() => editItem(index)} height="20" width="20" />
                    </span>
                    <span className="action_buttons2">
                      <img src="Delete.png" alt="delete" title="Delete this Text" onClick={() => removeItem(index)} height="20" width="20" />
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </b>
        <br />
        {window.innerWidth >= 600 && <button className="to-do_button" onClick={onClickAdd} >Add</button>}
        {/* {myarray.length >= 5 && <button onClick={removeAll} className="to-do_button">Remove All</button>} */}
      </center>
    </div>
  );
}

export default App;


